import React, {useEffect, useState} from 'react';
import {OutlinedInput, 
  InputLabel, 
  MenuItem, 
  FormControl, 
  ListItemText,
  Select,
  Checkbox,
} from '@material-ui/core';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 400,
    },
  },
};

export function SelectMultiple({ filterType, filterOptions, filters, group, applyFilters }) {
  const activeFilters = filters.map((filter, i) => filter.name);
  const [checked, setChecked] = useState([filters])

  useEffect(() => {
    if (checked) {
      // Find if the item is already in filters
      const isAlreadyFiltered = filters.some(filter => filter.name === checked);

      if (isAlreadyFiltered) {
        // If checked item exists, remove it
        const newFilters = filters.filter(filter => filter.name !== checked);
        applyFilters(newFilters);
      } else {
        // If checked item doesn't exist, add it
        const newFilters = [...filters, { name: checked, group: group }];
        applyFilters(newFilters);
      }
    }
  }, [checked]);

  return (
    <div style={{margin: '20px 20px 20px 0'}}>
      <FormControl fullWidth>
        <InputLabel style={{paddingLeft: 10}}>{ filterType }</InputLabel>
        <Select
          multiple
          onClose={applyFilters}
          value={filters.map((item, i) => item.group === group && item.name)}
          input={<OutlinedInput label={ filterType } />}
          renderValue={(selected) => selected}
          MenuProps={MenuProps}
        >
          {filterOptions.map((type) => (
            <MenuItem key={type} value={type}>
              <Checkbox checked={activeFilters.indexOf(type) > -1} onChange={(e) => setChecked(type)}/>
              <ListItemText primary={type} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    </div>
  );
}
