import React, { useEffect, useState } from 'react';
import {
  OutlinedInput,
  InputLabel,
  MenuItem,
  FormControl,
  ListItemText,
  Select,
  Checkbox,
} from '@material-ui/core';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 400,
    },
  },
};

export function SelectMultiple({ filterType, filterOptions, filters, setFilters, group }) {
  const handleChange = (event) => {
      const {
          target: { value },
      } = event;

      // Update filters state in parent component
      setFilters(typeof value === 'string' ? value.split(',') : value);
  };

  return (
      <div style={{margin: '20px 20px 20px 0'}}>
          <FormControl fullWidth>
              <InputLabel style={{paddingLeft: 10}}>{filterType}</InputLabel>
              <Select
                  multiple
                  value={filters}
                  onChange={handleChange}
                  input={<OutlinedInput label={filterType} />}
                  renderValue={(selected) => selected.join(', ')}
                  MenuProps={MenuProps}
              >
                  {filterOptions.map((option) => (
                      <MenuItem key={option} value={option}>
                          <Checkbox checked={filters.indexOf(option) > -1} />
                          <ListItemText primary={option} />
                      </MenuItem>
                  ))}
              </Select>
          </FormControl>
      </div>
  );
}
