import React, {useEffect, useState} from 'react';
import {OutlinedInput, 
  InputLabel, 
  MenuItem, 
  FormControl, 
  ListItemText,
  Select,
  Checkbox,
} from '@material-ui/core';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 400,
    },
  },
};

export function SelectMultiple({ filterType, filterOptions, filters, setFilters }) {
  const [checked, setChecked] = useState(filters.map(filter => filter.name));

  // Update checked state when filters prop changes
  useEffect(() => {
    setChecked(filters.map(filter => filter.name));
  }, [filters]);

  // Effect to apply filters when checked changes
  useEffect(() => {
    const newFilters = filterOptions.filter((option) => checked.indexOf(option) > -1);
    setFilters(newFilters); // Call to set filters in the parent
  }, [checked, filterOptions, setFilters]);

  const handleToggle = (value) => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <div style={{ margin: '20px 20px 20px 0' }}>
      <FormControl fullWidth>
        <InputLabel style={{ paddingLeft: 10 }}>{filterType}</InputLabel>
        <Select
          multiple
          value={checked}
          input={<OutlinedInput label={filterType} />}
          renderValue={(selected) => selected.join(', ')}
          MenuProps={MenuProps}
        >
          {filterOptions.map((type) => (
            <MenuItem key={type} value={type}>
              <Checkbox checked={checked.indexOf(type) > -1} onChange={() => handleToggle(type)} />
              <ListItemText primary={type} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    </div>
  );
}
